DROP DATABASE IF EXISTS pubg;
SET NAMES UTF8;
CREATE DATABASE pubg CHARSET=UTF8;
USE pubg;
CREATE TABLE pubg_user(
	uid INT PRIMARY KEY AUTO_INCREMENT,
	uname VARCHAR(32),
	upwd VARCHAR(32),
	email VARCHAR(32),
	gender INT
);
CREATE TABLE pubg_prop(
	pid INT PRIMARY KEY AUTO_INCREMENT,
	pkind VARCHAR(32),
	pckind VARCHAR(32),
	pname VARCHAR(32),
	img VARCHAR(128),
	pdetail VARCHAR(128)
);
CREATE TABLE pubg_detail(
	did INT PRIMARY KEY AUTO_INCREMENT,
	dkind VARCHAR(32),
	dtitle VARCHAR(128),
	dtime VARCHAR(32),
	dauthor VARCHAR(32),
	detail TEXT
);
